import React, { useEffect, useState } from 'react';
import './App.css';
import { BrowserRouter, Route, Routes, } from 'react-router-dom';
import Register from './components/auth/Register';
import Login from './components/auth/Login';
import Home from './components/view/Home';
import Admin from './components/view/Admin';
import EmailVerify from './components/emailVerify/EmailVerify';
import axios from 'axios';
import ProtectedRoute from './components/protectedRoute/ProtectedRoute';





function App() {

  const [user, setUser] = useState(null);

  const getLoginUser = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/logedin/user', { withCredentials: true });
      if (response.data) {
        setUser(response.data.user); // Set the user if data exists
      } else {
        setUser(null); // No user found
      }
    } catch (error) {
      console.error("Error fetching login user:", error);
      setUser(null); // Handle errors by setting the user to null
    }
  };

  useEffect(() => {
    getLoginUser()
  }, [])


  console.log("Loged in user detais:", user?.user)


  return (
    <>

      <BrowserRouter>
        <Routes>


          <Route path='/' element={<Login />} />
          <Route path='/register' element={<Register />} />
          <Route path='/user/verify/:token' element={<EmailVerify />} />
          {/* Protected Routes */}
          <Route
            path="/home"
            element={
              <ProtectedRoute user={user}>
                <Home />
              </ProtectedRoute>
            }
          />
          <Route
            path="/admin"
            element={
              <ProtectedRoute user={user}>
                <Admin />
              </ProtectedRoute>
            }
          />


        </Routes>
    </BrowserRouter >

    </>
  );
}

export default App;
