import { Navigate } from 'react-router-dom';

type ProtectedRouteProps = {
    user: any; // Adjust `any` to your actual user type, e.g., `UserType`
    children: React.ReactNode; // Represents the children components
  };

  const ProtectedRoute: React.FC<ProtectedRouteProps> = ({ user, children }) => {
    console.log("proted User")
    // Check if the user is authenticated
    if (!user || user.length === 0) {
      // If not authenticated, redirect to login
      return <Navigate to="/" />;
    }
    // If authenticated, render the children components (protected route)
    return <>{children}</>;
  };

  export default ProtectedRoute;
