import React, { useEffect, useState } from 'react';
import profile from '../../pic/profile.png';
import '../view/chatDashBoard.css';
import { useDispatch } from 'react-redux';
import { messageFail } from '../store/userSlice';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const ChatDashboard = () => {


  const [message, setMessage] = useState<string>('');
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const { id } = useParams()
  const [messages, setMessages] = useState([]); // State to store the chat messages
  const [loading, setLoading] = useState(true); // State to manage loading state



  const sendMessage = async (e: any) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        `http://localhost:5001/api/message/${id}`,
        { message },
        { withCredentials: true }
      );
      console.log(response.data);
      setMessage('');
    } catch (error) {

      // Typecast error as AxiosError to access its properties
      const axiosError = error as any; // Cast as `any` if you're unsure
      const errorResponse = axiosError?.response?.data.message;

      // Dispatch error message if it exists
      if (errorResponse === 'Please login to access') {
        dispatch(messageFail(errorResponse));
        navigate('/')
      } else {
        console.error('An unexpected error occurred:', error);
      }
    }
  };



  useEffect(() => {
      const fetchMessages = async () => {
          try {
              const response = await axios.get(`http://localhost:5001/messages/${chatWithId}`, {
                  withCredentials: true,
              });
              setMessages(response.data.messages); // Store retrieved messages
          } catch (error) {
              console.error('Error fetching messages:', error);
          } finally {
              setLoading(false); // Set loading to false
          }
      };

      fetchMessages();
  }, []); // Re-fetch messages when chatWithId changes

  return (
    <div className="chat-dashboard-container">
      {/* Profile Header */}
      <div className="header border-bottom d-flex align-items-center p-3">
        <img src={profile} alt="User Profile" className="profile-img" />
        <h5 className="ms-3">User Name</h5>
      </div>

      {/* Messages Section */}
      <div className="messages-container p-3">
        {/* Receiver's Message */}
        <div className="receiver-message">
          <p className="message-text">Hello! How are you?</p>
          <p className="message-time">10:30 AM</p>
        </div>

        {/* Sender's Message */}
        <div className="sender-message ms-auto">
          <p className="message-text">I'm doing great, thanks! How about you?</p>
          <p className="message-time text-white">10:32 AM</p>
        </div>
      </div>


      <form onSubmit={sendMessage}>
        <div className="message-input-container p-2 d-flex align-items-center">
          <input
            type="text"
            className="form-control me-2"
            placeholder="Type your message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button type='submit' className="btn btn-primary">Send</button>
        </div>
      </form>
    </div>
  );
};

export default ChatDashboard;
