import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';

import Login from './Login';
import Register from './Register';
import EmailVerify from './EmailVerify';
import Home from './Home';
import Admin from './Admin';
import ProtectedRoute from './ProtectedRoute'; // Import the ProtectedRoute component

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Add loading state

  // Fetch the logged-in user details
  const getLoginUser = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/logedin/user', {
        withCredentials: true,
      });
      setUser(response.data.user || null); // Set user if found, or null otherwise
    } catch (error) {
      console.error('Error fetching user data:', error);
      setUser(null); // Set user to null on error
    } finally {
      setLoading(false); // Set loading to false after the request is complete
    }
  };

  useEffect(() => {
    getLoginUser();
  }, []);

  console.log('Logged in user details:', user);

  if (loading) {
    return <div>Loading...</div>; // Show loading indicator while fetching user data
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/user/verify/:token" element={<EmailVerify />} />
        {/* Protected Routes */}
        <Route
          path="/home"
          element={
            <ProtectedRoute user={user}>
              <Home />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin"
          element={
            <ProtectedRoute user={user}>
              <Admin />
            </ProtectedRoute>
          }
        />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
