import React, { useEffect, useState } from 'react';
import './App.css';
import { BrowserRouter, Route, Routes, } from 'react-router-dom';
import Register from './components/auth/Register';
import Login from './components/auth/Login';
import Home from './components/view/Home';
import Admin from './components/view/Admin';
import EmailVerify from './components/emailVerify/EmailVerify';
import axios from 'axios';
import ProtectedRoute from './components/protectedRoute/ProtectedRoute';





function App() {

  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Add loading state

  // Fetch the logged-in user details
  const getLoginUser = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/logedin/user', {
        withCredentials: true,
      });
      setUser(response.data.user || null); // Set user if found, or null otherwise
    } catch (error) {
      console.error('Error fetching user data:', error);
      setUser(null); // Set user to null on error
    } finally {
      setLoading(false); // Set loading to false after the request is complete
    }
  };

  useEffect(() => {
    getLoginUser();
  }, []);

  console.log('Logged in user details:', user);

  if (loading) {
    return <div>Loading...</div>; // Show loading indicator while fetching user data
  }


  return (
    <>

      <BrowserRouter>
        <Routes>


          <Route path='/' element={<Login />} />
          <Route path='/register' element={<Register />} />
          <Route path='/user/verify/:token' element={<EmailVerify />} />
          {/* Protected Routes */}
          <Route
            path="/home"
            element={
              <ProtectedRoute user={user}>
                <Home />
              </ProtectedRoute>
            }
          />
          <Route
            path="/admin"
            element={
              <ProtectedRoute user={user}>
                <Admin />
              </ProtectedRoute>
            }
          />


        </Routes>
    </BrowserRouter >

    </>
  );
}

export default App;
