import { Navigate } from "react-router-dom";

type ProtectedRouteProps = {
  user: any; // Adjust based on your user type, e.g., `UserType`
  children: React.ReactNode; // Represents the children components
};

const ProtectedRoute: React.FC<ProtectedRouteProps> = ({ user, children }) => {
  console.log("Protected route user:", user);

  // Handle cases where user data is being fetched
  if (user === null) {
    return <div>Loading...</div>; // Placeholder while checking authentication
  }

  // Redirect to login if not authenticated
  if (!user || Object.keys(user).length === 0) {
    return <Navigate to="/" />;
  }

  // If authenticated, render the protected content
  return <>{children}</>;
};

export default ProtectedRoute;
